    --gradient: linear-gradient(
        to right,
        #0756e9,
        #7a2694,
        #3b1de9,
        #902926
    );
}

*{
    padding: 0;
    margin: 0;
}
body{
    background-image: var(--gradient);
    background-size: 400%;
    background-position: left;
    transition: background-position 10s;
    animation: backgroundAnimation 20s infinite alternate;
    font-family: 'Fredoka One', cursive;
}
@keyframes backgroundAnimation{
    0%{background-position: left;}
    100%{background-position: right;}
}
header{
    height: 10vh;
    font-size: 64px;
    color: #fff;
    letter-spacing: 3px;
    width: 100%;
    text-align: center;
}
.panel{
    min-height: 90vh;
    min-width: 100vw;
    display: flex;
    align-items: flex-start;
    
    %part{
        width: 50%;
        min-height: 60vh;
        box-sizing: border-box;
        margin: 100px;
        background-color: transparent;
    }
    
    &__left{
        @extend%part;
        border: 10px solid white;
        border-radius: 20px;
        display: flex;


        
        &__aiHand{    
            background-color:orange;    
            width: 50%;
            
        }
        &__userHand{
    
            background-color: purple;
            width: 50%;
        }
    }
    &__right{
        @extend%part;
        display: flex;
        flex-direction: column;
        justify-content: flex-start;
        text-align: center;
        color: white;
        
        &__weapons{ 
            min-height: 30vh;
            border: 10px solid rgba(255, 255, 255, 0);
            border-radius: 20px;
            display: flex;
            justify-content: space-evenly;
            align-items: center;

            
            &__item{
                padding: 20px;
                border-radius: 20px;
                width: 20%;
                cursor: pointer;
                background-color: rgba(128, 128, 128, 0.349);
                border: 5px solid rgb(212, 210, 210) ;
                transition: all .1s ease-in-out;

                & img{
                    max-height: 10vh;

                }
                &:hover{
                    transform: scale(1.07);
                    background-color: rgba(100, 223, 52, 0.733);
                    border-color: #fff;
                }
            }
        }
        &__startbtn{
            width: 15vw;
            padding: 10px 0 ;
            background-color: transparent;
            border-style: solid;
            border-radius: 30px;
            border-color: #fff;
            font-size: 30px;
            border-width: 5px;
            align-self: center;
            transition: all .1s ease-in-out;
            color: #fff;
            cursor: pointer;

            &:hover{
                transform: scale(1.07);
                background-color: rgba(100, 223, 52, 0.733);
                border-color: #fff;
            }
        }
    }

}
